import "./App.css";
import { useEffect, useState } from "react";
import { Link, Route, Routes } from "react-router-dom";
import { search } from "./BooksAPI";

function SearchBooks() {
    const [searchInput, setSearchInput] = useState('');
    const [searchResult, setSearchResult] = useState([]);

    useEffect(() => {
            const doSearchBooks = async () =>{
                const res = await search(searchInput,'20');
                setSearchResult(res);
            };
    
            doSearchBooks();
    }, [searchInput])

    const OnSearchBooks = (event) => {
        setSearchInput({searchInput: event.target.value}, function ()  {console.log(event.target.value);});
        console.log(event.target.value);
    }

    return (
        <div className="search-books">
          <div className="search-books-bar">
          <Link to="/">
            <a
              className="close-search"
            >
              Close
            </a>
          </Link>  
          <div className="search-books-input-wrapper">
                <input
                type="text"
                placeholder="Search by title, author, or ISBN new"
                value={searchInput}
                onChange={OnSearchBooks}
                />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">

            {Array.isArray(searchResult) ?  searchResult.map((book) =>
                                    <li key={book.id}>
                                    <div className="book">
                                        <div className="book-top">
                                        <div
                                            className="book-cover"
                                            style={{
                                            width: 128,
                                            height: 193,
                                            backgroundImage: 'url(' +
                                                book?.imageLinks?.thumbnail + ')',
                                            }}
                                        ></div>
                                        <div className="book-shelf-changer">
                                            <select>
                                            <option value="none" disabled>
                                                Move to...
                                            </option>
                                            <option value="currentlyReading">
                                                Currently Reading
                                            </option>
                                            <option value="wantToRead">Want to Read</option>
                                            <option value="read">Read</option>
                                            <option value="none">None</option>
                                            </select>
                                        </div>
                                        </div>
                                        <div className="book-title">{book.title}</div>
                                        <div className="book-authors">{book.authors}</div>
                                    </div>
                                </li>
                            ) : null}

            </ol>
          </div>
        </div>
    );

}

export default SearchBooks;
