import "./App.css";
import { Link, Route, Routes } from "react-router-dom";
import { useEffect, useState } from "react";
import { getAll, update } from "./BooksAPI";
import Book from "./Book";


function MainPage() {
     
    const [allShelfBook, setAllShelfBook] = useState([]);  
    const [reloadPage, setReloadPage] = useState('');

    const handleChange = (event,book) => {
        console.log("change");
        const doUpdateShelfBook = async (book,shelf) => {
            const res = await update(book,shelf);
            setReloadPage(Date().toLocaleString());
            console.log(res);
        }

        if (event.target.value !== 'none') {
            console.log(event.target.value);
            doUpdateShelfBook(book,event.target.value);
        }
      }

    useEffect(() => {
            setReloadPage(reloadPage);
            const dogetAllShelfBooks = async () =>{
                const res = await getAll();
                setAllShelfBook(res);
            };
            dogetAllShelfBooks();
    }, [reloadPage])


    return (
        <div className="app">
            <div className="list-books">
            <div className="list-books-title">
                <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
                <div>
                    <div className="bookshelf">
                        <h2 className="bookshelf-title">Currently Reading</h2>
                        <div className="bookshelf-books">
                            <ol className="books-grid">

                            {allShelfBook.filter((bookShelf) => bookShelf.shelf === 'currentlyReading').map((book) =>
                                <Book book={book} key={book.id} ></Book>
                            )}
                            </ol>
                        </div>
                    </div>

                <div className="bookshelf">
                    <h2 className="bookshelf-title">Want to Read</h2>
                    <div className="bookshelf-books">
                    <ol className="books-grid">
                    {allShelfBook.filter((bookShelf) => bookShelf.shelf === 'wantToRead').map((book) =>
                                <li key={book.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            <div
                                                className="book-cover"
                                                style={{
                                                    width: 128,
                                                    height: 193,
                                                    backgroundImage:'url(' +
                                                    book?.imageLinks?.thumbnail  + ')',
                                                }}
                                            ></div>
                                            <div className="book-shelf-changer">
                                                <select onChange={(event) => handleChange(event,book)}>
                                                    <option value="none" disabled>
                                                        Move to...
                                                    </option>
                                                    <option value="" hidden={true}></option>
                                                    <option value="currentlyReading">
                                                        Currently Reading
                                                    </option>
                                                    <option value="wantToRead" disabled>Want to Read</option>
                                                    <option value="read">Read</option>
                                                    <option value="none">None</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="book-title">{book.title}</div>
                                        <div className="book-authors">{book.authors}</div>
                                    </div>
                                </li>)}
                    </ol>
                    </div>
                </div>
                <div className="bookshelf">
                    <h2 className="bookshelf-title">Read</h2>
                    <div className="bookshelf-books">
                    <ol className="books-grid">
                    {allShelfBook.filter((bookShelf) => bookShelf.shelf === 'read').map((book) =>
                                <li key={book.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            <div
                                                className="book-cover"
                                                style={{
                                                    width: 128,
                                                    height: 193,
                                                    backgroundImage:'url(' +
                                                    book?.imageLinks?.thumbnail  + ')',
                                                }}
                                            ></div>
                                            <div className="book-shelf-changer">
                                                <select onChange={(event) => handleChange(event,book)}>
                                                    <option value="none" disabled>
                                                        Move to...
                                                    </option>
                                                    <option value="" hidden={true}></option>
                                                    <option value="currentlyReading">
                                                        Currently Reading
                                                    </option>
                                                    <option value="wantToRead">Want to Read</option>
                                                    <option value="read" disabled>Read</option>
                                                    <option value="none">None</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="book-title">{book.title}</div>
                                        <div className="book-authors">{book.authors}</div>
                                    </div>
                                </li>)}
                    </ol>
                    </div>
                </div>
                </div>
            </div>
            <div className="open-search">
                <Link to="/search">
                <a >Add a book</a>
                </Link>
            </div>
            </div>
        </div>
    );
}

export default MainPage;
